import { ADD_TODO, REMOVE_TODO } from "./../contants/todoContant";

const addTodo = ({payload}) => {
  const item = {
    id: payload.id,
    title: payload.title
  }
  return {
    type: ADD_TODO,
    item
  };
};

const removeTodo = (payload) => {
  return {
    type: REMOVE_TODO,
    payload
  };
};

export { addTodo, removeTodo };
